
--CREATES TABLES---------------------------------------------------------
create column table "PLANNING_TOOL"."IMPORT_L5_L6" (
	IMPORT_L5_L6_ID bigint not null primary key generated by default as IDENTITY,
	DESCRIPTION NVARCHAR(255),


	created_date_tz timestamp default CURRENT_TIMESTAMP,
	modified_date_tz timestamp,
	created_user_id bigint not null,
	modified_user_id bigint,
	enabled tinyint default 1,
	deleted tinyint default 0,
    FOREIGN KEY (created_user_id) REFERENCES "PLANNING_TOOL".USER(user_id)
);

create column table "PLANNING_TOOL"."DICTIONARY_L5_L6" (
	DICTIONARY_L5_L6_ID bigint not null primary key generated by default as IDENTITY,
	PATH NVARCHAR(255),
	KEY_L5_L6 NVARCHAR(255),
	VALUE_L5_L6 NVARCHAR(2000),
	HIERARCHY_LEVEL_ID TINYINT,

	created_date_tz timestamp default CURRENT_TIMESTAMP,
	modified_date_tz timestamp,
	created_user_id bigint not null,
	modified_user_id bigint,
	enabled tinyint default 1,
	deleted tinyint default 0,
    FOREIGN KEY (created_user_id) REFERENCES "PLANNING_TOOL".USER(user_id)
);

create column table "PLANNING_TOOL"."MAP_L5_L6" (
	MAP_L5_L6_ID bigint not null primary key generated by default as IDENTITY,
	TYPE TINYINT,
	COLUMN_NAME NVARCHAR(255),
	CSV_COLUMN_NAME NVARCHAR(255),
	DATA_TYPE NVARCHAR(25),
	FOREIGN_TABLE_NAME NVARCHAR(255),
	FOREIGN_COLUMN_REFERENCE NVARCHAR(255),
	FOREIGN_COLUMN_FILTER NVARCHAR(255),
	OTHER_CONDITION NVARCHAR(1000),

	created_date_tz timestamp default CURRENT_TIMESTAMP,
	modified_date_tz timestamp,
	created_user_id bigint not null,
	modified_user_id bigint,
	enabled tinyint default 1,
	deleted tinyint default 0,
    FOREIGN KEY (created_user_id) REFERENCES "PLANNING_TOOL".USER(user_id)
);

CREATE COLUMN TABLE "PLANNING_TOOL"."UPLOAD_L5_L6_LOG" ("UPLOAD_L5_L6_LOG_ID" BIGINT CS_FIXED GENERATED BY DEFAULT AS IDENTITY NOT NULL ,
	 "CSV_COLUMN_NAME" NVARCHAR(5000),
	 "COLUMN_VALUE" NVARCHAR(5000),
	 "STATE" TINYINT CS_INT,
	 "COMMENTS" NVARCHAR(5000),
	 "IMPORT_ID" BIGINT,
	 "CREATED_DATE_TZ" LONGDATE CS_LONGDATE DEFAULT CURRENT_TIMESTAMP,
	 "MODIFIED_DATE_TZ" LONGDATE CS_LONGDATE,
	 "CREATED_USER_ID" BIGINT CS_FIXED NOT NULL ,
	 "MODIFIED_USER_ID" BIGINT CS_FIXED,
	 "ENABLED" TINYINT CS_INT DEFAULT 1,
	 "DELETED" TINYINT CS_INT DEFAULT 0,
	 PRIMARY KEY ("UPLOAD_L5_L6_LOG_ID")) UNLOAD PRIORITY 5 AUTO MERGE
;
ALTER TABLE "PLANNING_TOOL"."UPLOAD_L5_L6_LOG" ADD FOREIGN KEY ( "CREATED_USER_ID" ) REFERENCES "PLANNING_TOOL"."USER" ("USER_ID") ON UPDATE RESTRICT ON DELETE RESTRICT
;
ALTER TABLE "PLANNING_TOOL"."UPLOAD_L5_L6_LOG" ADD FOREIGN KEY ( "IMPORT_ID" ) REFERENCES "PLANNING_TOOL"."IMPORT_L5_L6" ("IMPORT_L5_L6_ID") ON UPDATE RESTRICT ON DELETE RESTRICT
;

