

INSERT INTO CONFIGURATION(KEY, VALUE, DESCRIPTION, CREATED_USER_ID) VALUES ('DebugMode',0,'IF IS TRUE THE TRACER IS ENABLE',1);

CREATE COLUMN TABLE "PLANNING_TOOL"."TRACE_PROFILING" (
        "TRACE_PROFILING_ID" bigint not null primary key generated by default as IDENTITY,
        "TYPE" TINYINT,
        "CALLER" NVARCHAR(100),
        "METHOD" NVARCHAR(100),
        "DATA" NVARCHAR(3000),
        "USER" integer,
        "TIMESTAMP" bigint,
        "TRANSACTION" NVARCHAR(25),
        "PATH" NVARCHAR(500)

);

