CREATE COLUMN TABLE "PLANNING_TOOL"."INTERLOCK_REQUEST_REGION" (
        "INTERLOCK_REQUEST_REGION_ID" bigint not null primary key generated by default as IDENTITY,
        "INTERLOCK_REQUEST_ID" BIGINT NOT NULL,
        "REGION_ID" BIGINT NOT NULL,

        "CREATED_DATE_TZ" timestamp default CURRENT_TIMESTAMP,
        "MODIFIED_DATE_TZ" timestamp,
        "CREATED_USER_ID" bigint not null,
        "MODIFIED_USER_ID" bigint,
        "ENABLED" tinyint default 1,
        "DELETED" tinyint default 0,

        FOREIGN KEY ("CREATED_USER_ID") REFERENCES "PLANNING_TOOL"."USER"("USER_ID"),
        FOREIGN KEY ("INTERLOCK_REQUEST_ID") REFERENCES "PLANNING_TOOL"."INTERLOCK_REQUEST"("INTERLOCK_REQUEST_ID"),
        FOREIGN KEY ("REGION_ID") REFERENCES "PLANNING_TOOL"."REGION"("REGION_ID")
);